import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import './CustomCSS.css'; 
import * as parsedData from './JSON_Dat.json';
import SideNav, { NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';

import { Container, Button, Modal, ModalBody, ModalHeader, ModalFooter } from 'mdbreact';

import 'font-awesome/css/font-awesome.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';

import '@trendmicro/react-sidenav/dist/react-sidenav.css';

class App extends Component { 

constructor(props) {
  super(props);
  this.state = {content: "",
  modal: false};
  this.reloadContent = this.reloadContent.bind(this)
  this.showModal = this.showModal.bind(this)
  this.hideModal = this.hideModal.bind(this)
}

//#region Helpers 

showModal() {
  this.setState({ showModal: true });
};

hideModal() {
  this.setState({ showModal: false });
};

reloadContent(path) {
  this.setState((state) => ({content : path}))
}

extractLink(str) {
  var link;

  if (str.indexOf("http") > -1) {
      link = str.split('http')[1];
  }
  else {
      link = "";
  }

  return "http" + link;
}

isImage(str) {
  if (str.toString().includes("drive.google")) //Check if it is an image
  {
    return true;
  }

  return false;
}

googleImage(link){
  return link.replace("open", "uc");
}

closeModal(id) {
  ReactDOM.getElementById(id).style.display = "none";
}


toggle = () => {
  this.setState({
    modal: !this.state.modal
  });
}

Modal(header, innerText, id) {
    const CustomModal = () => 
    // ( <div class="modal fade hide" id={id} role="dialog">
    //     <div class="modal-dialog">
    //       <div class="modal-content">
    //         <div class="modal-header">
    //           <button type="button" class="close" data-dismiss="modal">&times;</button>
    //           <h4 class="modal-title">{header}</h4>
    //         </div>
    //         <div class="modal-body">
    //           <p>{innerText}</p>
    //         </div>
    //         <div class="modal-footer">
    //           <button type="button" class="btn btn-default" data-dismiss="modal" onClick={() => this.closeModal(id)}>Close</button>
    //         </div>
    //       </div>
    //     </div>
    //   </div>);
    (
      <Container>
        <Button onClick={this.toggle}>Modal</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
            (...)
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.toggle}>Close</Button>{' '}
            <Button color="primary">Save changes</Button>
          </ModalFooter>
        </Modal>
      </Container>);

  return ReactDOM.createPortal( <CustomModal/>, document.getElementById('root'));
}
//#endregion

HeaderFunc = () => {

  const children = []
  var HeaderLogo 
  const base = (<nav class="white" role="navigation">
                  <div class="nav-wrapper container">
                    <a id="logo-container" href="www.google.com" className="brand-logo">&nbsp; SARVA 3.0</a>
                    <ul class="right hide-on-med-and-down">
                      {children}
                    </ul>
                  </div>
                </nav>)

  for (let x in parsedData.default.ideas){
    for (let y in parsedData.default.ideas[x])
    { 
      if (parsedData.default.ideas[x][y] === "NavBar") 
      {
        if(parsedData.default.ideas[x].ideas !== "undefined"){
          for (let z in parsedData.default.ideas[x].ideas){
            if (parsedData.default.ideas[x].ideas[z].title === "Menu"){
              for (let ddl in parsedData.default.ideas[x].ideas[z].ideas){
                const innerArray = []
                if (parsedData.default.ideas[x].ideas[z].ideas[ddl].ideas !== "undefined")
                {
                  for (let inner in parsedData.default.ideas[x].ideas[z].ideas[ddl].ideas)
                  {
                    const ddlStyle = { 
                                        fontSize: '12px',
                                        indent: '50px'
                                     };

                     innerArray.push(<li style={ddlStyle}><a href="www.google.com" >{parsedData.default.ideas[x].ideas[z].ideas[ddl].ideas[inner].title}</a></li>)
                  }
                }
                if (innerArray.length > 0)  
                {
                  children.push(<li class="sub-menu-parent" tab-index="0">
                                  <a href="www.google.com">{parsedData.default.ideas[x].ideas[z].ideas[ddl].title} &#8659;</a>
                                  <ul class="sub-menu">
                                    {innerArray}
                                  </ul>
                                </li>)
                }
                else {
                  children.push(<li styled><a href="www.google.com">{parsedData.default.ideas[x].ideas[z].ideas[ddl].title}</a></li>)
                }
              }
            }
            else if (parsedData.default.ideas[x].ideas[z].title === "Logo"){
              for (let count in parsedData.default.ideas[x].ideas[z].ideas) {
                if (this.isImage(parsedData.default.ideas[x].ideas[z].ideas[count].title)){
                  HeaderLogo = this.googleImage(this.extractLink(parsedData.default.ideas[x].ideas[z].ideas[count].title))
                }
              }
              children.push(<img id="menu-logo" alt="img not loaded" src={HeaderLogo}></img> )
            }
          } 
        }
      }
    }
  }
  return base 
}

SideNavFunc = () => {

var InnerDdl = [], Items = []
var Glyhicon = "fa fa-fw fa-home"

const Base = <SideNav componentClass="sideNav"><SideNav.Toggle /><SideNav.Nav>{ Items }</SideNav.Nav></SideNav>

for (let o in parsedData.default.ideas)
{
  for (let u in parsedData.default.ideas[o])
  {
    if (parsedData.default.ideas[o][u] === "SideNav") 
    {
      if(parsedData.default.ideas[o].ideas !== "undefined")
      {
        for (let i in parsedData.default.ideas[o].ideas)
        {
          //Rember to check if you need to initialize anything

          //Here check for a glyphicon, using home for now

          if (parsedData.default.ideas[o].ideas[i].ideas !== "undefined")
          {
            InnerDdl = []
            for (let w in parsedData.default.ideas[o].ideas[i].ideas)
            {
              //Check what type it is, if its a modal, new tab, replace content and breadcrumb

              //If Modal

              const ModalId = '#' + parsedData.default.ideas[o].ideas[i].ideas[w].title.replace(/\s/g, '');
              InnerDdl.push(<NavItem eventKey={parsedData.default.ideas[o].ideas[i].ideas[w].id} onClick={this.showModal}>
                              <NavText style={{color: 'black !important'}}>
                                <a type="button" class="black-text" data-toggle="modal" data-target={ModalId}>{parsedData.default.ideas[o].ideas[i].ideas[w].title}</a>
                                {this.Modal(parsedData.default.ideas[o].ideas[i].ideas[w].title, "More Test", parsedData.default.ideas[o].ideas[i].ideas[w].title.replace(/\s/g, ''))}
                              </NavText>
                            </NavItem>)

              ///////////////////////////////////////////////////

              //If new tab link  

              // InnerDdl.push(<NavItem eventKey={parsedData.default.ideas[o].ideas[i].ideas[w].id} >
              //                 <NavText style={{color: 'black !important'}}>
              //                 <a href="http://google.com" rel="noopener nereferrer" class="black-text" target='_blank'>{parsedData.default.ideas[o].ideas[i].ideas[w].title}</a>
              //                 </NavText>
              //               </NavItem>)

              ///////////////////////////////////////////////////

              //If content reload with new breadcrumb

              // InnerDdl.push(<NavItem eventKey={parsedData.default.ideas[o].ideas[i].ideas[w].id} onClick= {() => this.reloadContent(parsedData.default.ideas[o].ideas[i].ideas[w].title)}>
              //                 <NavText style={{color: 'black !important'}}>
              //                 {parsedData.default.ideas[o].ideas[i].ideas[w].title}
              //                 </NavText>
              //               </NavItem>)

              ///////////////////////////////////////////////////

              // InnerDdl.push(<NavItem eventKey={parsedData.default.ideas[o].ideas[i].ideas[w].id} onClick= {() => popModal(parsedData.default.ideas[o].ideas[i].ideas[w].title, "")}>>
              //                 <NavText style={{color: 'black !important'}}>
              //                   {parsedData.default.ideas[o].ideas[i].ideas[w].title}
              //                 </NavText>
              //                 <dialog id="myDialog">This is a dialog window</dialog>
              //               </NavItem>)
            }

            /////////////////////////////////////////

            Items.push(<NavItem eventKey={ parsedData.default.ideas[o].ideas[i].id }>
                        <NavIcon>
                          <i className={Glyhicon} style={{ fontSize: '1.75em', color: '#fff' }} />
                        </NavIcon>
                        <NavText>{ parsedData.default.ideas[o].ideas[i].title } </NavText>
                        {InnerDdl}
                      </NavItem>) 
          }
          else{ 
            Items.push(<NavItem eventKey={ parsedData.default.ideas[o].ideas[i].id }>
                        <NavIcon>
                          <i className={Glyhicon} style={{ fontSize: '1.75em' }} />
                        </NavIcon>
                        <NavText> {parsedData.default.ideas[o][u].ideas[i].title} </NavText>
                      </NavItem>)
          }
        }
      }
    }
  }
}


return (Base)
}

ContentFunc = (Page) => {
  //Make use of page var to reload page content

  var Heading = "", Subtext = "" 

  if (Page !== ""){
    Heading = <h2 class="header center text-darken-4">{Page}</h2>
    Subtext = <div class="row center">
                <h5 class="header col s12 text-darken-4">Updated, Interactive Tools are now available for detailed assessment of the impact of pressures and drivers on important human, physical, and environmental assets. These tools are currently focused on Impact Assessment, but will in time extend to allow determination of Risk and Vulnerability</h5>
              </div>

  }

  var Base = <div id="index-banner" class="parallax-container" >
                  <div class="section no-pad-bot">
                    <div class="container">
                      <br></br>
                      <br></br>
                      { Heading }
                      { Subtext }
                    </div>
                  </div> 
                </div>

  return Base
}

ContentWidgets = () => {

  const Text = <div class="section no-pad-bot">
                  <div class="container">
                    <div class="row center">
                      <h2 class="header center brown-text text-darken-3">Distributed, High Resolution Data</h2>
                      <h5 class="header col s12 brown-text text-darken-3">Distributed, high-resolution data can be annotated and linked from any standardised service in the web and included into our profiling and impact assessment tools. Very large datasets can be accommodated without deterioration in service - thanks to an innovative distributed data processing breakthrough.</h5>      
                    </div>
                    <div class="row center">
                      <a href="#data-modal" id="download-button" type="button" class="btn btn-primary">Read More</a>
                    </div>
                    <br></br>
                  </div>
                </div>

  const Widgets = <div class="section">
                    <div class="row">
                      <div class="col s12 m4">
                        <div class="icon-block center">
                          <button type="button" class="btn btn-primary" data-target="search-modal">Search</button>   
                          <h5 class="center">Search and Discover Resources</h5>
                          <p class="light center">Use simple or advanced tools to search for data meeting your criteria.</p>
                        </div>
                      </div>

                      <div class="col s12 m4">
                        <div class="icon-block center">
                          <button type="button" class="btn btn-primary" data-target="theme-modal" onClick={ () => this.reloadContent('Themes')}>Themes</button>            
                          <h5 class="center">Explore Available Resources by Sector or Theme</h5>
                          <p class="light center">Browse, preview, and access data and narratives in popular collections and in common themes.</p>
                        </div>
                      </div>

                      <div class="col s12 m4">
                        <div class="icon-block center">
                          <button type="button" class="btn btn-primary" data-target="contr-modal">Contribute</button>  
                          <h5 class="center">Tell Us About Your Area of Interest</h5>
                          <p class="light center"><a href="www.google.com">Register</a> as a user and contribute data, assessments, and media.</p>
                        </div>
                      </div>
                      </div>
                  </div>

  //const Background = <img id="img2" src="background_image_4.png" alt="Unsplashed background img 2" ></img>

  const Base = 
  <div>
    <div class="container widgets">
      { Widgets }
    </div>
    <div class="parallax-container valign-wrapper" >
      {Text}
    </div>
  </div>

  return ( Base )
}

FooterFunc = () => { 

  const children = [] 
  const FooterStyle = { 'padding-left': '1%', 'padding-right': '1%' }
  const base = (<footer class="page-footer font-small indigo" style={ FooterStyle }><div class="row">{children}</div></footer>)
  var cardTitle
  var cardSub 

  for (let x in parsedData.default.ideas){
    for (let y in parsedData.default.ideas[x])
    { 
      if (parsedData.default.ideas[x][y] === "Footer") 
      {
        if(parsedData.default.ideas[x].ideas !== "undefined")
        {
          for (let z in parsedData.default.ideas[x].ideas)
          {

            cardTitle = ""
            cardSub = "";
            var type = false;
            var linkList = []

            for (let r in parsedData.default.ideas[x].ideas[z]){
              if (r === "title")
              {
                cardTitle = parsedData.default.ideas[x].ideas[z][r];
              }
              else if (r === "attr")
              {
                for (let t in parsedData.default.ideas[x].ideas[z][r])
                {
                  for (let u in parsedData.default.ideas[x].ideas[z][r][t])
                  {
                    if (u === "text")
                    {
                      cardSub = <p class="white-text light"> { parsedData.default.ideas[x].ideas[z][r][t][u] } </p>
                    }
                    else if (u === "")
                    {

                    }
                  }
                }
              }
              else if (r === "ideas")
              {
                for (let t in parsedData.default.ideas[x].ideas[z][r])
                {
                  //Check link type here and determine if its a logo or something
                  var link = (this.extractLink(parsedData.default.ideas[x].ideas[z][r][t].title));

                  if (this.isImage(link)) { //It is an image, or more accurately it is a google drive doc
                    cardSub = <img src={ link.replace("open", "uc") } alt="img not loaded"></img>
                  } 
                  else { //it is not a google drive link
                    type = true;
                    linkList.push(<li><a class="white-text light" href={ link }>{ parsedData.default.ideas[x].ideas[z][r][t].title.split("http")[0] }</a></li>)
                  }
                }
              }
            }
            if (type) {
              children.push(<div class="col l3 s12">
                              <h5 class="white-text light"> { cardTitle } </h5>
                              <ul>{ linkList }</ul>
                            </div>)
            }
            else {
            children.push(<div class="col l3 s12">
                            <h5 class="white-text light"> { cardTitle } </h5>
                            { cardSub }
                          </div>)
            }
          } 
        }
      }
    }
  }
  return base 
}

  render() {

    return (
      <div className="App" class="App"> 
        {this.SideNavFunc()}
        {this.HeaderFunc()}
        {this.ContentFunc(this.state.content)} 
        {this.ContentWidgets()}       
        {this.FooterFunc()}
      </div>
    );
  }
}

export default App;
