                                     }; 

                     innerArray.push(<li style={ddlStyle} key={parsedData.default.ideas[x].ideas[z].ideas[ddl].ideas[inner].id}><a href="www.google.com" >{parsedData.default.ideas[x].ideas[z].ideas[ddl].ideas[inner].title}</a></li>)
                  }
                }
