import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import './CustomCSS.css'; 
import * as parsedData from './JSON_Dat.json';
import { slide as Menu } from 'react-burger-menu'
import '@trendmicro/react-sidenav/dist/react-sidenav.css';

//#region Helpers 

function extractLink(str) {
  var link;

  if (str.indexOf("http") > -1) {
      link = str.split('http')[1];
  }
  else {
      link = "";
  }

  return "http" + link;
}

function linkType(str){

  if (str.toString().includes("drive.google")) //Check if it is an image
  {
    return true;
  }

  return false;
}

function openNav() {
  // document.getElementById("mySidenav").style.width = "250px";
  // document.getElementById("main").style.marginLeft = "250px";
  // document.body.style.backgroundColor = "rgba(0,0,0,0.4)";
}

function closeNav() {
  // document.getElementById("mySidenav").style.width = "0";
  // document.getElementById("main").style.marginLeft= "0";
  // document.body.style.backgroundColor = "white";
}

// document.getElementById(this.state.baction).addPrecent(10);
// this.refs[this.state.baction].addPrecent(10);

//#endregion

function HeaderFunc(){

  const children = []
  //var HeaderLogo
  const base = (<nav class="white" role="navigation">
                  <div class="nav-wrapper container">
                    <a id="logo-container" href="www.google.com" class="brand-logo">&nbsp; SARVA 3.0</a>
                    <ul class="right hide-on-med-and-down">
                      {children}
                    </ul>
                  </div>
                </nav>)

  for (let x in parsedData.default.ideas){
    for (let y in parsedData.default.ideas[x])
    { 
      if (parsedData.default.ideas[x][y] === "NavBar") 
      {
        if(parsedData.default.ideas[x].ideas !== "undefined"){
          for (let z in parsedData.default.ideas[x].ideas){
            if (parsedData.default.ideas[x].ideas[z].title === "Menu"){
              for (let ddl in parsedData.default.ideas[x].ideas[z].ideas){
                const innerArray = []
                if (parsedData.default.ideas[x].ideas[z].ideas[ddl].ideas !== "undefined")
                {
                  for (let inner in parsedData.default.ideas[x].ideas[z].ideas[ddl].ideas)
                  {
                    //const ddlStyle = "style=font-size:12px; text-indent:50px"
                     innerArray.push(<li><a href="www.google.com" >{parsedData.default.ideas[x].ideas[z].ideas[ddl].ideas[inner].title}</a></li>)
                  }
                }
                if (innerArray.length > 0)  
                {
                  children.push(<li class="sub-menu-parent" tab-index="0">
                                  <a href="www.google.com">{parsedData.default.ideas[x].ideas[z].ideas[ddl].title} &#8659;</a>
                                  <ul class="sub-menu">
                                    {innerArray}
                                  </ul>
                                </li>)
                }
                else {
                  children.push(<li styled><a href="www.google.com">{parsedData.default.ideas[x].ideas[z].ideas[ddl].title}</a></li>)
                }
              }
            }
            else if (parsedData.default.ideas[x].ideas[z].title === "Logo"){
              children.push(<img id="menu-logo" alt="img not loaded" src={logo}></img> )
            }
          } 
        }
      }
    }
  }
  return base 
}

function SideNavFunc(){

    return (
    <Menu>
      <a id="home" className="white-text" href="/">Home</a>
      <a id="about" className="white-text" href="/about">About</a>
      <a id="contact" className="white-text menu-item" href="/contact">Contact</a>
      <a className="menu-item--small" href="">Settings</a>
    </Menu>
  );

  return (
    <div>
  <ul ref="slide-out" class="side-nav SideNav">      
  <img class="sideNavLogo" src="image/logo_saeon.png"></img>
     <li class="no-padding">
      <ul class="collapsible collapsible-accordion">
        <li>
          <a class="collapsible-header grey lighten-2">SARVA<i class="material-icons">arrow_drop_down</i></a>
          <div class="collapsible-body">
            <ul>
              <li><a class="modal-trigger SideNavModal" href="#using-modal">Using SARVA</a></li> 
              <li><a class="modal-trigger SideNavModal" href="#profile-modal">Profiler</a></li>                  
              <li><a class="modal-trigger SideNavModal" href="#atlas-modal">Atlas</a></li>                   
              <li><a class="modal-trigger SideNavModal" href="#disco-modal">Finding Data</a></li>            
            </ul>
          </div>
        </li>
      </ul>
    </li>  

     <li class="no-padding">
      <ul class="collapsible collapsible-accordion">
        <li>
          <a class="collapsible-header">Global Change<i class="material-icons">arrow_drop_down</i></a>
          <div class="collapsible-body">
            <ul>
              <li><a class="modal-trigger SideNavModal" href="#frame-modal" >Semantic Framework</a></li>
              <li><a class="modal-trigger SideNavModal" href="#evidence-modal" >Data and Evidence</a></li>
              <li><a href="#!" class="SideNavModal">Work Programme</a></li>            
            </ul>
          </div>
        </li>
      </ul>
    </li> 

     <li class="no-padding">
      <ul class="collapsible collapsible-accordion">
        <li>
          <a class="collapsible-header">Linked Systems<i class="material-icons">arrow_drop_down</i></a>
          <div class="collapsible-body">
            <ul>
              <li><a class="modal-trigger SideNavModal" href="#nccrd-modal" >NCCRD</a></li>
              <li><a class="modal-trigger SideNavModal" href="#" >Events and Disasters</a></li>         
            </ul>
          </div>
        </li>
      </ul>
    </li>       

     <li class="no-padding">
      <ul class="collapsible collapsible-accordion">
        <li>
          <a class="collapsible-header">Archive<i class="material-icons">arrow_drop_down</i></a>
          <div class="collapsible-body">
            <ul>
              <li><a class="modal-trigger SideNavModal" href="#saws-modal" >SAWS Climate Atlas</a></li>
              <li><a class="modal-trigger SideNavModal" href="#beeh-modal" >Atlas of Agrohydrology</a></li>
              <li><a class="modal-trigger SideNavModal" href="#sarv2-modal" >SARVA 2.0</a></li>
              <li><a class="modal-trigger SideNavModal" href="#sarv1-modal" >SARVA 1.0</a></li>
              <li><a class="modal-trigger SideNavModal" href="#read-modal">Reading Risk</a></li>

            </ul>
          </div>
        </li>
      </ul>
    </li>
    <img class="odpLogo" src="image/logo_odp.png"></img>      
</ul>
<a href="#" data-activates="slide-out" onclick={openNav(this)} class="button-collapse "><i class="small material-icons grey-text ">menu</i></a></div>)
}

function ContentFunc() {
  // const style = "display: block; transform: translate3d(-50%, 39px, 0px);"

  const Heading = <h2 class="header center text-darken-4">Updated, Interactive Tools</h2>
  const Subtext = <div class="row center">
                    <h5 class="header col s12 text-darken-4">Updated, Interactive Tools are now available for detailed assessment of the impact of pressures and drivers on important human, physical, and environmental assets. These tools are currently focused on Impact Assessment, but will in time extend to allow determination of Risk and Vulnerability</h5>
                  </div>    

  const Base = <div id="index-banner" class="parallax-container" >
                  <div class="section no-pad-bot">
                    <div class="container">
                      <br></br>
                      <br></br>
                      { Heading }
                      { Subtext }
                    </div>
                  </div> 
                </div>

  return Base
}

function ContentWidgets(){

  const Text = <div class="section no-pad-bot">
                  <div class="container">
                    <div class="row center">
                      <h2 class="header center brown-text text-darken-3">Distributed, High Resolution Data</h2>
                      <h5 class="header col s12 brown-text text-darken-3">Distributed, high-resolution data can be annotated and linked from any standardised service in the web and included into our profiling and impact assessment tools. Very large datasets can be accommodated without deterioration in service - thanks to an innovative distributed data processing breakthrough.</h5>      
                    </div>
                    <div class="row center">
                      <a href="#data-modal" id="download-button" class="center darken-2 waves-effect waves-light btn modal-trigger">Read More</a>
                    </div>
                    <br></br>
                  </div>
                </div>

  const Widgets = <div class="section">
                    <div class="row">
                      <div class="col s12 m4">
                        <div class="icon-block center">
                          <button class=" center grey waves-effect waves-light btn modal-trigger" data-target="search-modal">Search</button>   
                          <h5 class="center">Search and Discover Resources</h5>
                          <p class="light center">Use simple or advanced tools to search for data meeting your criteria.</p>
                        </div>
                      </div>

                      <div class="col s12 m4">
                        <div class="icon-block center">
                          <button class=" center grey waves-effect waves-light btn modal-trigger" data-target="theme-modal" onclick="showForm('Themes')">Themes</button>            
                          <h5 class="center">Explore Available Resources by Sector or Theme</h5>
                          <p class="light center">Browse, preview, and access data and narratives in popular collections and in common themes.</p>
                        </div>
                      </div>

                      <div class="col s12 m4">
                        <div class="icon-block center">
                          <button class=" center grey waves-effect waves-light btn modal-trigger" data-target="contr-modal">Contribute</button>  
                          <h5 class="center">Tell Us About Your Area of Interest</h5>
                          <p class="light center"><a href="www.google.com">Register</a> as a user and contribute data, assessments, and media.</p>
                        </div>
                      </div>
                      </div>
                  </div>

  //const Background = <img id="img2" src="background_image_4.png" alt="Unsplashed background img 2" ></img>

  const Base = 
  <div>
    <div class="container widgets">
      { Widgets }
    </div>
    <div class="parallax-container valign-wrapper">
      {Text}
      {/* <div class="parallax">
        { Background }
      </div> */}
    </div>
  </div>

  return ( Base )
}

function FooterFunc(){ 

  const children = [] 
  const base = (<footer class="page-footer grey"><div class="row">{children}</div></footer>)
  var cardTitle
  var cardSub 

  for (let x in parsedData.default.ideas){
    for (let y in parsedData.default.ideas[x])
    { 
      if (parsedData.default.ideas[x][y] === "Footer") 
      {
        if(parsedData.default.ideas[x].ideas !== "undefined")
        {
          for (let z in parsedData.default.ideas[x].ideas)
          {

            cardTitle = ""
            cardSub = "";
            var type = false;
            var linkList = []

            for (let r in parsedData.default.ideas[x].ideas[z]){
              if (r === "title")
              {
                cardTitle = parsedData.default.ideas[x].ideas[z][r];
              }
              else if (r === "attr")
              {
                for (let t in parsedData.default.ideas[x].ideas[z][r])
                {
                  for (let u in parsedData.default.ideas[x].ideas[z][r][t])
                  {
                    if (u === "text")
                    {
                      cardSub = <p class="white-text light"> { parsedData.default.ideas[x].ideas[z][r][t][u] } </p>
                    }
                    else if (u === "")
                    {

                    }
                  }
                }
              }
              else if (r === "ideas")
              {
                for (let t in parsedData.default.ideas[x].ideas[z][r])
                {
                  //Check link type here and determine if its a logo or something
                  var link = (extractLink(parsedData.default.ideas[x].ideas[z][r][t].title));

                  if (linkType(link)) { //It is an image, or more accurately it is a google drive doc
                    cardSub = <img src={ link.replace("open", "uc") } alt="img not loaded"></img>
                  } 
                  else { //it is not a google drive link
                    type = true;
                    linkList.push(<li><a class="white-text light" href={ link }>{ parsedData.default.ideas[x].ideas[z][r][t].title.split("http")[0] }</a></li>)
                  }
                }
              }
            }
            if (type) {
              children.push(<div class="col l3 s12">
                              <h5 class="white-text light"> { cardTitle } </h5>
                              <ul>{ linkList }</ul>
                            </div>)
            }
            else {
            children.push(<div class="col l3 s12">
                            <h5 class="white-text light"> { cardTitle } </h5>
                            { cardSub }
                          </div>)
            }
          } 
        }
      }
    }
  }
  return base 
}

class App extends Component {

  render() {

    return (
      <div className="App" class="App"> 
        {SideNavFunc()}
        {HeaderFunc()}
        {ContentFunc()} 
        {ContentWidgets()}       
        {FooterFunc()}
      </div>
    );
  }
}

export default App;
