import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import './CustomCSS.css'; 
import * as data from './JSON_Dat.json';

var word = JSON.stringify(data);
const data2 = '{ "formatVersion": 3, "id": "root", "ideas": { "1": { "title": "NavBar", "id": 1, "attr": { "position": [ -1015, -496, 1 ], "style": { "background": "#3366FF" } }, "ideas": { "1": { "title": "Logo", "id": "5.137b.610236dcc-c9b6.4cc16d678", "ideas": { "1": { "title": " https://drive.google.com/open?id=0ByxQT6Sr1O7ZdGVRMHZzWXYtaTFDU0g4czF1VENMNXRHTWhN", "id": "23.137b.610236dcc-c9b6.4cc16d678" } }, "attr": { "style": { "background": "#3366FF" } } }, "11": { "title": "Menu", "id": "6.137b.610236dcc-c9b6.4cc16d678", "attr": { "position": [ 97, 55.80000001192093, 1 ], "style": { "background": "#3366FF" } }, "ideas": { "1": { "title": "Home", "id": "7.137b.610236dcc-c9b6.4cc16d678" }, "2": { "title": "Services", "id": "8.137b.610236dcc-c9b6.4cc16d678" }, "3": { "title": "Products", "id": "9.137b.610236dcc-c9b6.4cc16d678" }, "4": { "title": "Themes", "id": "11.137b.610236dcc-c9b6.4cc16d678", "ideas": { "1": { "title": "Biodiversity", "id": "12.137b.610236dcc-c9b6.4cc16d678" }, "2": { "title": "Trends", "id": "13.137b.610236dcc-c9b6.4cc16d678" } } } } } } }, "2": { "title": "Footer", "id": "2.137b.610236dcc-c9b6.4cc16d678", "attr": { "position": [ -980, -82, 1 ], "style": { "background": "#0000FF" } }, "ideas": { "1": { "title": "Technology", "id": "14.137b.610236dcc-c9b6.4cc16d678", "attr": { "note": { "index": 11, "text": "The DST funds the SAEON Open Data Platform (ODP) and associated dissemination portals. Developed by SAEON on behalf of DST, DEA, and other stakeholders." } } }, "2": { "title": "SAEON ODP", "id": "17.137b.610236dcc-c9b6.4cc16d678" }, "12": { "title": "Funding", "id": "21.137b.610236dcc-c9b6.4cc16d678", "attr": { "position": [ 114.79998779296875, 152.5999984741211, 1 ] }, "ideas": { "1": { "title": "https://drive.google.com/open?id=0B18ulA7MmoWOcTQ0SkhIRDBVU3c", "id": "22.137b.610236dcc-c9b6.4cc16d678" } } }, "1.5": { "title": "Legal", "id": "16.137b.610236dcc-c9b6.4cc16d678", "ideas": { "1": { "title": "Disclaimer http://noframe.media.dirisa.org/wiki-1/disclaimer", "id": "18.137b.610236dcc-c9b6.4cc16d678" }, "2": { "title": "Privacy http://noframe.media.dirisa.org/wiki-1/privacy-statement", "id": "19.137b.610236dcc-c9b6.4cc16d678" }, "3": { "title": "Conditions of Use http://noframe.media.dirisa.org/wiki-1/conditions-of-use", "id": "20.137b.610236dcc-c9b6.4cc16d678" } } } } }, "3": { "title": "SideNav", "id": "3.137b.610236dcc-c9b6.4cc16d678", "attr": { "position": [ -1078.2000122070312, -233.8000030517578, 1 ], "style": { "background": "#0000FF" } } }, "4": { "title": "Content", "id": "4.137b.610236dcc-c9b6.4cc16d678", "attr": { "position": [ -1080, -301, 1 ], "style": { "background": "#0000FF" } } } }, "title": "" }'

const parsedData = JSON.parse(word);

//#region Helpers
function myFunction(json) {
  let obj = JSON.parse(json);

  var ul = (<ul></ul>)
  var iconUrl = obj.ideas

  for (let x in obj.ideas[1]) {
      if (x == 'attr') {
          if (obj.ideas[1][x].hasOwnProperty("icon")) {
              var logo = "#"
              if (obj.ideas[1][x].icon.hasOwnProperty("metaData")) {

                  if (obj.ideas[1][x].icon.metaData.remoteUrl.includes(".png") || obj.ideas[1][x].icon.metaData.remoteUrl.includes(".jpg") || obj.ideas[1][x].icon.metaData.remoteUrl.includes(".jpeg")) {
                      logo = obj.ideas[1][x].icon.metaData.remoteUrl;
                  }
                  else if (obj.ideas[1][x].icon.metaData.original.includes(".png") || obj.ideas[1][x].icon.metaData.original.includes(".jpg") || obj.ideas[1][x].icon.metaData.original.includes(".jpeg")) {
                      logo = obj.ideas[1][x].icon.metaData.original;
                  }
              }
              let Taggy = Taggy + React.createElement(<a id="navBar_left" class="navbar-left"><img id="logo" class="logo"></img></a>)
              //document.getElementById("logo").setAttribute("src", logo.toString());
              break;
          }
      }

  }
  for (let x in obj.ideas[1].ideas) {

      for (let y in obj.ideas[1].ideas[x]) {
          if (obj.ideas[1].ideas[x].hasOwnProperty(y)) {
              if (y === "title") {
                  var li = React.createElement("li")

                  if (obj.ideas[1].ideas[x].hasOwnProperty('ideas')) {
                      li.setAttribute("class", "nav-item dropdown")

                      var a = React.createElement("a");
                      a.innerText = obj.ideas[1].ideas[x][y];

                      ul.appendChild(li);

                      a.setAttribute("class", "nav-link dropdown-toggle");
                      a.setAttribute("href", "#");
                      a.setAttribute("role", "button");
                      a.setAttribute("data-toggle", "dropdown");
                      a.setAttribute("aria-haspopup", "true");
                      a.setAttribute("aria-expanded", "false");

                      var div = React.createElement("div");
                      div.setAttribute("class", "dropdown-menu");
                      div.setAttribute("aria-labelledby", "navbarDropdown");

                      li.appendChild(a);
                      li.appendChild(div)
                      for (a in obj.ideas[1].ideas[x].ideas) {
                          var innerA = React.createElement("a");
                          innerA.setAttribute("class", "dropdown-item");

                          var innerTxt = obj.ideas[1].ideas[x].ideas[a].title;

                          if (innerTxt.includes("http")) {

                              var contentTag = React.getElementById("content");
                              var iframe = React.createElement("iframe");
                              iframe.setAttribute("src", "http" + innerTxt.split('http')[1]);
                              contentTag.appendChild(iframe)

                              innerA.innerText = innerTxt.split('http')[0];
                          }
                          else
                              innerA.innerText = innerTxt;

                          div.appendChild(innerA);
                      }
                  }
                  else {
                      //li.setAttribute("class", "nav-item")
                      li += {'class' : 'nav-item'};

                      var a = document.createElement("a");
                      a.innerText = obj.ideas[1].ideas[x][y];

                      ul.appendChild(li);

                      a.setAttribute("class", "nav-link");
                      a.setAttribute("href", "#");

                      li.appendChild(a);
                  }

              }
          }
      }
  }
} 

function extractLink(str) {
  var link;

  if (str.indexOf("http") > -1) {
      link = str.split('http')[1];
  }
  else {
      link = "";
  }

  return "http" + link;
}

function linkType(str){

  if (str.toString().includes("drive.google")) //Check if it is an image
  {
    return true;
  }

  return false;
}
//#endregion

function HeaderFunc(){

  const children = []
  var HeaderLogo
  const base = (<nav class="white" role="navigation">
                  <div class="nav-wrapper container">
                    <a id="logo-container" href="#" class="brand-logo">&nbsp; SARVA 3.0</a>
                    <ul class="right hide-on-med-and-down">
                      {children}
                    </ul>
                  </div>
                </nav>)

  for (let x in parsedData.default.ideas){
    for (let y in parsedData.defaultideas[x])
    { 
      if (parsedData.ideas[x][y] == "NavBar") 
      {
        if(parsedData.ideas[x].ideas != "undefined"){
          for (let z in parsedData.ideas[x].ideas){
            if (parsedData.ideas[x].ideas[z].title == "Menu"){
              for (let ddl in parsedData.ideas[x].ideas[z].ideas){
                const innerArray = []
                if (parsedData.ideas[x].ideas[z].ideas[ddl].ideas != "undefined")
                {
                  for (let inner in parsedData.ideas[x].ideas[z].ideas[ddl].ideas)
                  {
                    const ddlStyle = "style=font-size:12px; text-indent:50px"
                     innerArray.push(<li><a href="#" >{parsedData.ideas[x].ideas[z].ideas[ddl].ideas[inner].title}</a></li>)
                  }
                }
                if (innerArray.length > 0)  
                {
                  children.push(<li class="sub-menu-parent" tab-index="0">
                                  <a href="#">{parsedData.ideas[x].ideas[z].ideas[ddl].title} &#8659;</a>
                                  <ul class="sub-menu">
                                    {innerArray}
                                  </ul>
                                </li>)
                }
                else {
                  children.push(<li styled><a>{parsedData.ideas[x].ideas[z].ideas[ddl].title}</a></li>)
                }
              }
            }
            else if (parsedData.ideas[x].ideas[z].title === "Logo"){
              children.push(<img id="menu-logo" src={logo}></img> )
            }
          } 
        }
      }
    }
  }
  return base 
}

function ContentFunc() {
  const stylele = "display: block; transform: translate3d(-50%, 39px, 0px);"
  return (<div id="index-banner" class="parallax-container" >
            <div class="section no-pad-bot">
              <div class="container">
                <br></br>
                <br></br>
                <h2 class="header center text-darken-4">Updated, Interactive Tools</h2>
                <div class="row center">
                  <h5 class="header col s12 text-darken-4">Updated, Interactive Tools are now available for detailed assessment of the impact of pressures and drivers on important human, physical, and environmental assets. These tools are currently focused on Impact Assessment, but will in time extend to allow determination of Risk and Vulnerability</h5>
                </div>    
              </div>
            </div> 
          </div>)
}

function ContentWidgets(){
  return (
  <div>
    <div class="container widgets">
      <div class="section">
        <div class="row">
          <div class="col s12 m4">
            <div class="icon-block center">
              <button class=" center grey waves-effect waves-light btn modal-trigger" data-target="search-modal">Search</button>   
              <h5 class="center">Search and Discover Resources</h5>
              <p class="light center">Use simple or advanced tools to search for data meeting your criteria.</p>
            </div>
          </div>

          <div class="col s12 m4">
            <div class="icon-block center">
              <button class=" center grey waves-effect waves-light btn modal-trigger" data-target="theme-modal" onclick="showForm('Themes')">Themes</button>            
              <h5 class="center">Explore Available Resources by Sector or Theme</h5>
              <p class="light center">Browse, preview, and access data and narratives in popular collections and in common themes.</p>
            </div>
          </div>

          <div class="col s12 m4">
            <div class="icon-block center">
              <button class=" center grey waves-effect waves-light btn modal-trigger" data-target="contr-modal">Contribute</button>  
              <h5 class="center">Tell Us About Your Area of Interest</h5>
              <p class="light center"><a href="#">Register</a> as a user and contribute data, assessments, and media.</p>
            </div>
          </div>
          </div>
      </div>
    </div>
    <div class="parallax-container valign-wrapper">
    <div class="section no-pad-bot">
      <div class="container">
        <div class="row center">
          <h2 class="header center brown-text text-darken-3">Distributed, High Resolution Data</h2>
          <h5 class="header col s12 brown-text text-darken-3">Distributed, high-resolution data can be annotated and linked from any standardised service in the web and included into our profiling and impact assessment tools. Very large datasets can be accommodated without deterioration in service - thanks to an innovative distributed data processing breakthrough.</h5>      
        </div>
        <div class="row center">
          <a href="#data-modal" id="download-button" class="center darken-2 waves-effect waves-light btn modal-trigger">Read More</a>
        </div>
        <br></br>
      </div>
    </div>
    <div class="parallax">
      <img id="img2" src="image\background_image_4.png" alt="Unsplashed background img 2" ></img>
    </div>
  </div>
</div>
  )
}

function FooterFunc(){ 

  const children = [] 
  const base = (<footer class="page-footer grey"><div class="row">{children}</div></footer>)
  var cardTitle, cardText
  var cardSub 

  for (let x in parsedData.ideas){
    for (let y in parsedData.ideas[x])
    { 
      if (parsedData.ideas[x][y] === "Footer") 
      {
        if(parsedData.ideas[x].ideas !== "undefined")
        {
          for (let z in parsedData.ideas[x].ideas)
          {

            cardTitle = ""
            cardText = "";
            cardSub = "";
            var type = false;
            var linkList = []

            for (let r in parsedData.ideas[x].ideas[z]){
              if (r === "title")
              {
                cardTitle = parsedData.ideas[x].ideas[z][r];
              }
              else if (r === "attr")
              {
                for (let t in parsedData.ideas[x].ideas[z][r])
                {
                  for (let u in parsedData.ideas[x].ideas[z][r][t])
                  {
                    if (u === "text")
                    {
                      cardSub = <p class="white-text light"> { parsedData.ideas[x].ideas[z][r][t][u] } </p>
                    }
                    else if (u === "")
                    {

                    }
                  }
                }
              }
              else if (r === "ideas")
              {
                for (let t in parsedData.ideas[x].ideas[z][r])
                {
                  //Check link type here and determine if its a logo or something
                  var link = (extractLink(parsedData.ideas[x].ideas[z][r][t].title));

                  if (linkType(link)) { //It is an image, or more accurately it is a google drive doc
                    cardSub = <img src={ link.replace("open", "uc") } alt="img not loaded"></img>
                  } 
                  else { //it is not a google drive link
                    type = true;
                    linkList.push(<li><a class="white-text light" href={ link }>{ parsedData.ideas[x].ideas[z][r][t].title.split("http")[0] }</a></li>)
                  }
                }
              }
            }
            if (type) {
              children.push(<div class="col l3 s12">
                              <h5 class="white-text light"> { cardTitle } </h5>
                              <ul>{ linkList }</ul>
                            </div>)
            }
            else {
            children.push(<div class="col l3 s12">
                            <h5 class="white-text light"> { cardTitle } </h5>
                            { cardSub }
                          </div>)
            }
          } 
        }
      }
    }
  }
  return base 
}


class App extends Component {

  render() {

    return (
      <div className="App" class="App"> 
        {HeaderFunc()}
        {ContentFunc()} 
        {ContentWidgets()}       
        {FooterFunc()}
      </div>
    );
  }
}

export default App;
