  // const Widgets = <div class="section">
  //                   <div class="row">
  //                     <div class="col s12 m4">
  //                       <div class="icon-block center">
  //                         <button type="button" class="btn btn-primary" data-target="search-modal" onClick={ () => this.reloadContent('Search')}>Search</button>   
