                   {/* { Widgets } */}
                   <div class="section">
                      <div class="row">
                        {Widgets}
                      </div>
                    </div>
                 </div>
                 <div class="parallax-container valign-wrapper" >
                   {Text}
                 </div>
               </div>

  return ( Base )
}

FooterFunc = () => { 

  const children = [] 
  const FooterStyle = { 'padding-left': '1%', 'padding-right': '1%' }
  const base = (<footer class="page-footer font-small indigo" style={ FooterStyle }><div class="row">{children}</div></footer>)
  var cardTitle
  var cardSub 

  for (let x in parsedData.default.ideas){
    for (let y in parsedData.default.ideas[x])
    { 
      if (parsedData.default.ideas[x][y] === "Footer") 
      {
        if(parsedData.default.ideas[x].ideas !== "undefined")
        {
          for (let z in parsedData.default.ideas[x].ideas)
          {

            cardTitle = ""
            cardSub = "";
            var type = false;
            var linkList = []

            for (let r in parsedData.default.ideas[x].ideas[z]){
              if (r === "title")
              {
                cardTitle = parsedData.default.ideas[x].ideas[z][r];
              }
              else if (r === "attr")
              {
                for (let t in parsedData.default.ideas[x].ideas[z][r])
                {
                  for (let u in parsedData.default.ideas[x].ideas[z][r][t])
                  {
                    if (u === "text")
                    {
                      cardSub = <p class="white-text light"> { parsedData.default.ideas[x].ideas[z][r][t][u] } </p>
                    }
                    else if (u === "")
                    {

                    }
                  }
                }
              }
              else if (r === "ideas")
              {
                for (let t in parsedData.default.ideas[x].ideas[z][r])
                {
                  //Check link type here and determine if its a logo or something
                  var link = (this.extractLink(parsedData.default.ideas[x].ideas[z][r][t].title));

                  if (this.isImage(link)) { //It is an image, or more accurately it is a google drive doc
                    cardSub = <img src={ link.replace("open", "uc") } alt="img not loaded"></img>
                  } 
                  else { //it is not a google drive link
                    type = true;
                    linkList.push(<li><a class="white-text light" href={ link }>{ parsedData.default.ideas[x].ideas[z][r][t].title.split("http")[0] }</a></li>)
                  }
                }
              }
            }
            if (type) {
              children.push(<div class="col l3 s12">
                              <h5 class="white-text light"> { cardTitle } </h5>
                              <ul>{ linkList }</ul>
                            </div>)
            }
            else {
            children.push(<div class="col l3 s12">
                            <h5 class="white-text light"> { cardTitle } </h5>
                            { cardSub }
                          </div>)
            }
          } 
        }
      }
    }
  }
  return base 
}

  render() {

    return (
      <div className="App" class="App"> 
        {this.SideNavFunc()}
        {this.HeaderFunc()}
        {this.ContentFunc(this.state.content)} 
        {this.ContentWidgets()}       
        {this.FooterFunc()}
      </div>
    );
  }
}

